import React from 'react'
import logo from "./images/logo.png";
import FileBase64 from "react-file-base64";
import { useEffect } from "react"; //a hook that GIVES  "side-effects"
import { useHistory } from "react-router-dom"; // allows us to access our path / route history.
import Axios from "axios"; //allows us to make GET and POST requests from the browser.
import { useState } from "react"; //HERE we import useState Hook so we can add state to our functional components.
import verifyIcon from "./images/verify.svg";
import { useParams } from "react-router-dom"; // returns: an object of key/value pairs of URL parameters
import swal from 'sweetalert';

function Register() {

  const [prevPage, setprevPage] = useState(false);
  const [nexPage, setnexPage] = useState(true);
  const [verifyEmail, setVerifyEmail] = useState(true);
  const [code, setCode] = useState("");

  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");
  const [city, setCity] = useState("");
  const [state, setState] = useState("");
  const [zip, setZip] = useState("");
  const [cardName, setCardName] = useState("");
  const [creditCard, setCreditCard] = useState("");
  const [expMonth, setExpMonth] = useState("");
  const [expYear, setExpYear] = useState("");
  const [cvv, setCVV] = useState("");
  const [item, setItem] = useState({ image:''});


  function nextPage(e){
    e.preventDefault();
    setprevPage(true)
    setnexPage(false)
  }
  function createAccount(e){
    e.preventDefault();

    if(item.image==""){
      swal({
        title: "National ID is missing!",
        text: "Please upload your NID!",
        icon: "warning",
        button: "Got it",
       
      });
    }else{
      setnexPage(true)
      setVerifyEmail(false)


      Axios.post("http://localhost:5000/register",
      
      {
      
      firstname:firstname,
      lastname:lastname,
      password:password,
      phone:phone,
      email:email,
      address:address,
      city:city,
      state:state,
      zip:zip,
      cardName:cardName,
      creditCard:creditCard,
      expMonth:expMonth,
      expYear:expYear,
      cvv:cvv,
      image:item.image
       
      })
        .then((res) => {  
          console.log(res.data.user)

          localStorage.setItem("email",res.data.user.email)
          localStorage.setItem("userId",res.data.user._id)
   

        
        }) 
        .catch ((err) => {
                   
      
          if(err.response.data[27]=='1'){
         
            swal({
              title: "Email already exist!",
              text: "Please register new email!",
              icon: "warning",
              button: "Got it",
             
            });
            setTimeout(() => {
              window.location.reload()
            }, "3000")
         
