import React from 'react'
import logo from "./images/logo.png";
import { useEffect } from "react"; //a hook that GIVES  "side-effects"
import { useHistory } from "react-router-dom"; // allows us to access our path / route history.
import Axios from "axios"; //allows us to make GET and POST requests from the browser.
import { useState } from "react"; //HERE we import useState Hook so we can add state to our functional components.
import verifyIcon from "./images/verify.svg";
import { useParams } from "react-router-dom"; // returns: an object of key/value pairs of URL parameters
import swal from 'sweetalert';
function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");


  let history = useHistory(); //USE HISTORY  it will DETERMINED OUR PAST PATH.

  const loginNow =(e)=>{
    e.preventDefault();
  
    const data ={
        password: password,
        email: email
    }
    
    Axios.post("http://localhost:5000/login",data)
  
    .then(res =>{
  
       //IF AUTH IS = TRUE comes from the backend!
     if (res.data.auth){
  
       if(res.data.position ==="user"){
        console.log(res.data)
        localStorage.setItem("user", res.data.token);
        localStorage.setItem("email", res.data.email);
        localStorage.setItem("password", res.data.password);

        swal({
          title: "Login Successfully!",
          icon: "success",
          showConfirmButton: false,

        
         
        });
        setTimeout(() => {
          history.push("/dashboard")
        }, "2000")


  
       } else if(res.data.position ==="admin"){       
        localStorage.setItem("admin", res.data.token);
        localStorage.setItem("email", res.data.email);
        localStorage.setItem("password", res.data.password);
  
  
       }
       
   
     }
      
  
    })
     .catch(err =>{
      swal({
        text:"Account doesn't exist or still under review",
        icon: "warning",
     

      
       
      });
        
   })
  
  }

  const isLoaded = [true];
  useEffect(() => {

     if (isLoaded) {
      Axios.post("http://localhost:5000/login", 

      { 
        
        
        email: localStorage.getItem("email"),
        password: localStorage.getItem("password")
      }
      
      )
    
      
      .then((response) => {
        history.push("/dashboard")

     
      })
      .catch((error) => {
        history.push("/login")
      })

    }else{
      console.log("ERROR")
    }


  }, isLoaded);
  
  return (


    <main className="_widget login" >
      <center>
        <br></br>
      <section className="cf7cb0683 _prompt-box-outer cd488bb40" >
        <div className="cb158515f cc5801146"  >
          <div className="c835b06af">
            <header className="ca2efc7d8 c61e4fcc8">
              <div title="BlockFi" id="custom-prompt-logo"  />
              <img style={{width: '50%'}} src={logo}  />
              <h1 className="c3fa29956 c926e5028" style={{fontFamily:"'Arial",marginTop: '-18px'}}><b>Log in</b></h1>
              <div className="c2077a76c cac95ad60">
              </div>
            </header>
            <div className="c439d90a0 c37cd0ac8">
              <form method="POST" className="c0425e711 c322487ef">
                <input type="hidden" name="state" defaultValue="hKFo2SBNT3Jzak90Z0xocVFzV2h1Q0s3YmdFdXE4Wk9rdVYyOaFur3VuaXZlcnNhbC1sb2dpbqN0aWTZIElRbERlSmxmRkJ2MUk3TG5hYldpZDlUSzlUU09qZUt1o2NpZNkgS1JyVTFzTFgzd2R1a0JlRllrZG01ZkFzWWFsS2tnZUI" />
                <div className="cb0776e96 c55a4daea">
                  <div className="c91b28086">
                    <div className="input-wrapper _input-wrapper">
                      <div className="c02a080f8 c73989c48 text ca6eb1802" data-action-text data-alternate-action-text>
                      <label >
                      
                      Email address
                    </label>
                        <input className="input cd8c0df77 c519c8f96"   onChange={(event) => {
                                  setEmail(event.target.value);
                                }}inputMode="email" name="username" id="username" type="text" Placeholder="ðŸ“§ Your Email" required autoComplete="username" autoCapitalize="none" spellCheck="false" autofocus />
                       
                      
                      </div>
                    </div>
                    <div className="input-wrapper _input-wrapper">
                      <div className="c02a080f8 c73989c48 password cd890d100" data-action-text data-alternate-action-text>
                      <label style={{marginRight:"60%"}} htmlFor="password">
                          Password
                        </label>
                        <input  onChange={(event) => {
                                  setPassword(event.target.value);
                                }} className="input cd8c0df77 c640d2562"  placeholder="ðŸ‘ï¸ Your Password"name="password" id="password" type="password" required autoComplete="current-password" autoCapitalize="none" spellCheck="false" />
                       
                       
                        <button type="button" className="cd1df0865 ulp-button-icon c6cad41d5 _button-icon" data-action="toggle">
                          <span className="screen-reader-only password-toggle-label" data-label="toggle">Show Password</span>
                          <span className="c6989ffc0 password js-required" aria-hidden="true" />
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
                <p className="c73d89667 cd4743c67">
                  <a className="c82c0b9b3 cabd3c7de c8061fbcb" href="#">Forgot password?</a>
                </p>
                <div className="c6d7d2ace">
                  <button type="submit" name="action" value="default" onClick={loginNow} className="cd1df0865 c0b512bff c6cad41d5 c132a5a03 c571d5ab7">
                    Login
                  </button>
                </div>
                <p className="c73d89667 cd4743c67">
                  <a className="c82c0b9b3 cabd3c7de c8061fbcb" href="/register">Don't have an account yet?</a>
                </p>
              </form>
              <div className="ulp-alternate-action _alternate-action __s16nu9">
                <p className="c76075b34 c2fad6637 ca7b07586">
                  <a className="c82c0b9b3 c8061fbcb" href="">
                  </a>
                </p>
              </div>
            </div>
          </div>
        </div>
      </section>
      </center>
      <br></br>
    </main>
  );
}

export default Login
