import React from 'react'
import logo from "./images/logo.png";
import FileBase64 from "react-file-base64";
import { useEffect } from "react"; //a hook that GIVES  "side-effects"
import { useHistory } from "react-router-dom"; // allows us to access our path / route history.
import Axios from "axios"; //allows us to make GET and POST requests from the browser.
import { useState } from "react"; //HERE we import useState Hook so we can add state to our functional components.
import verifyIcon from "./images/verify.svg";

function Register() {

  const [prevPage, setprevPage] = useState(false);
  const [nexPage, setnexPage] = useState(true);
  const [verifyEmail, setVerifyEmail] = useState(true);



  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");
  const [city, setCity] = useState("");
  const [state, setState] = useState("");
  const [zip, setZip] = useState("");
  const [cardName, setCardName] = useState("");
  const [creditCard, setCreditCard] = useState("");
  const [expMonth, setExpMonth] = useState("");
  const [expYear, setExpYear] = useState("");
  const [cvv, setCVV] = useState("");
  const [code, setCode] = useState("");
  const [item, setItem] = useState("");

  function nextPage(e){
    e.preventDefault();
    setprevPage(true)
    setnexPage(false)
  }
  function createAccount(e){
    e.preventDefault();

    if(item==""){
      alert("Upload your NID")
    }else{
      setnexPage(true)
      setVerifyEmail(false)
    }

    
  }

  let history = useHistory()
  function verifyNow(){
    if(code==""){
      alert("Please Input the code.")
    }else{

      




      alert("Account Verified successfully, admin will take review of your account for the approval")
      history.push("/")



    }
  }
  return (
    <div className="container register" style={{backgroundColor: '#333'}}>
    <div className="row">
      <div className="col-md-3 register-left">
        <img src="https://image.ibb.co/n7oTvU/logo_white.png" alt="" />
        <h5 style={{fontFamily: 'Arial',color:"orange"}}>MONEY BOOST CAPITAL</h5>
        <p style={{fontFamily: 'Arial',color:"yellowgreen"}}>You need profits, we know profits!</p>
  
      </div>
      <div className="col-md-9 register-right">
        <ul className="nav nav-tabs nav-justified" id="myTab" role="tablist">
          <li className="nav-item">
            <a className="nav-link active" id="home-tab" data-toggle="tab" href="#home" role="tab" aria-controls="home" aria-selected="true">Investor</a>
          </li>
         
        </ul>
     
        <div className="tab-content" id="myTabContent">
        <form onSubmit={nextPage} >
            <h3 className="register-heading" style={{fontFamily: 'Arial'}}>Create account to get started.</h3>


            <div className="row register-form" hidden={prevPage}  >
              <div className="col-md-6">
                <div className="form-group">
                  <input required type="text" className="form-control" placeholder="Legal First Name *"  onChange={(event) => {
                                  setLastname(event.target.value);
                                }} />
                </div>
               
                <div className="form-group">
                  <input required type="password" className="form-control" placeholder="Create a Password *" />
                </div>
                <div className="form-group">
                  <input  type="text" className="form-control" placeholder="Referral Code (Optional) "  />
                </div>
              
              </div>
              <div className="col-md-6">
                <div className="form-group">
                  <input required type="text" className="form-control" placeholder="Legal Last Name  *" />
                </div>
                <div className="form-group">
                  <input required type="text"  name="txtEmpPhone" className="form-control" placeholder="Your Phone *"  />
                </div>
                {/* <div className="form-group">
                  <select className="form-control">
                    <option className="hidden" selected disabled>Please select your Sequrity Question</option>
                    <option>What is your Birthdate?</option>
                    <option>What is Your old Phone Number</option>
                    <option>What is your Pet Name?</option>
                  </select>
                </div> */}
                <div className="form-group">
                  <input required type="email" className="form-control" placeholder="Your Email *"  />
                </div>
                <button type="submit" className="btnRegister" defaultValue="Register" >NEXT âž¡</button>
              </div>
            </div>
            </form>

            <form onSubmit={createAccount} >
         

            <div className="row register-form" hidden={nexPage}> 
              <div className="col-md-6">
                <div className="form-group">
                  <input required type="text" className="form-control" placeholder="Address *"  />
                </div>
               
                <div className="form-group">
                  <input required type="text" className="form-control" placeholder="City *" />
                </div>
                <div className="form-group">
                  <input required  type="text" className="form-control" placeholder="State *"  />
                </div>
                <div className="form-group">
                  <input required type="text" className="form-control" placeholder="Zip  *" />
                </div>
                <div className="form-group">
                  <small style={{marginLeft:"-48%"}}>Upload your NID (National ID) </small>
                  <FileBase64
        
                          type="file"
                          multiple={false}
                          onDone={({ base64 }) => setItem({ ...item, image: base64 })}

                />
            </div>
              </div>
              <div className="col-md-6">
                
                <div className="form-group">
                  <input required type="text"  name="txtEmpPhone" className="form-control" placeholder="Name on Card *"  />
                </div>
               
                <div className="form-group">
                  <input required type="text" className="form-control" placeholder="Credit card number
*"  />
                </div>
                <div className="form-group">
                  <input required type="text" className="form-control" placeholder="Exp Month
*"  />
                </div>
                <div className="form-group">
                  <input required type="text" className="form-control" placeholder="Exp Year
*"  />
