import React from 'react'
import { useEffect } from "react"; //a hook that GIVES  "side-effects"
import { useHistory } from "react-router-dom"; // allows us to access our path / route history.
import Axios from "axios"; //allows us to make GET and POST requests from the browser.
import { useState } from "react"; //HERE we import useState Hook so we can add state to our functional components.

function Admin() {
  let history = useHistory(); //USE HISTORY  it will DETERMINED OUR PAST PATH.
  const [userDetails, setUserDetails] = useState([]);

  const isLoaded = [true];
useEffect(() => {

  let format = (JSON.parse(localStorage.getItem("userDetails")));

  setUserDetails(format)
   if (isLoaded) {
    Axios.post("http://localhost:5000/login", 

    { 
      
      
      email: localStorage.getItem("email"),
      password: localStorage.getItem("password")
    }
    
    )
  
    
    .then((response) => {
      if(response.data.position=="admin"){
        console.log("User is  validated")


        
      }else{
        history.push("/login")
        window.location.reload();
      }


   
    })
    .catch((error) => {
      history.push("/login")
    })

  }else{
    console.log("ERROR")
  }


}, isLoaded);


function Logout(){
  localStorage.clear()
  window.location.reload()

}

  return (
    <div className="wrapper d-flex align-items-stretch">
    <nav id="sidebar">
      <div className="p-4 pt-5">
        <a href="#" className="img logo rounded-circle mb-5" style={{backgroundImage: 'url("https://www.pngmart.com/files/21/Admin-Profile-PNG-Photo.png)'}} />
        <ul className="list-unstyled components mb-5">
          <li className="active">
            <a href="#homeSubmenu" data-toggle="collapse" aria-expanded="false" className="dropdown-toggle">Home</a>
            <ul className="collapse list-unstyled" id="homeSubmenu">
         
              <li>
                <a href="#">#</a>
              </li>
              <li>
                <a href="#">#</a>
              </li>
            </ul>
          </li>
          <li>
            <a href="#">Investors</a>
          </li>
     
          <li>
            <a onClick={Logout} style={{cursor:"pointer"}}>Logout</a>
          </li>
        </ul>
      </div>
    </nav>
    {/* Page Content  */}
    <div id="content" className="p-4 p-md-5">
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="container-fluid">
          <button type="button" id="sidebarCollapse" className="btn btn-primary">
            <i className="fa fa-bars" />
            <span className="sr-only">Toggle Menu</span>
          </button>
          <button className="btn btn-dark d-inline-block d-lg-none ml-auto" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <i className="fa fa-bars" />
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            {/* <ul className="nav navbar-nav ml-auto">
              <li className="nav-item active">
                <a className="nav-link" href="#">Home</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#">About</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#">Portfolio</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#">Contact</a>
              </li>
            </ul> */}
          </div>
        </div>
      </nav>
      <h2 className="mb-4" style={{fontFamily: 'Arial'}}>Investors Request/s</h2>
  
    </div>
  </div>
  )
}

export default Admin
