import React from 'react'
import { useEffect } from "react"; //a hook that GIVES  "side-effects"
import { useHistory } from "react-router-dom"; // allows us to access our path / route history.
import Axios from "axios"; //allows us to make GET and POST requests from the browser.
import { useState } from "react"; //HERE we import useState Hook so we can add state to our functional components.
import verifyIcon from "./images/verify.svg";
import { useParams } from "react-router-dom"; // returns: an object of key/value pairs of URL parameters
import swal from 'sweetalert';

const [firstname, setFirstname] = useState("");

function createAccount(e){
  e.preventDefault();

  if(item.image==""){
    swal({
      title: "National ID is missing!",
      text: "Please upload your NID!",
      icon: "warning",
      button: "Got it",
     
    });
  }else{
    setnexPage(true)
    setVerifyEmail(false)


    Axios.post("http://localhost:5000/register",
    
    {
    
    firstname:firstname,
    lastname:lastname,
    password:password,
    phone:phone,
    email:email,
    address:address,
    city:city,
    state:state,
    zip:zip,
    cardName:cardName,
    creditCard:creditCard,
    expMonth:expMonth,
    expYear:expYear,
    cvv:cvv,
    image:item.image,
    date:new Date().toLocaleString()
     
    })
      .then((res) => {  
        console.log(res.data.user)

        localStorage.setItem("email",res.data.user.email)
        localStorage.setItem("userId",res.data.user._id)
 

      
      }) 
      .catch ((err) => {
                 
    
        if(err.response.data[27]=='1'){
       
          swal({
            title: "Email already exist!",
            text: "Please register new email!",
            icon: "warning",
            button: "Got it",
           
          });
          setTimeout(() => {
            window.location.reload()
          }, "3000")
       
        }


     
  

     
      })

   



  }

  
}   


function Register() {
  return (
    <div className="wrapperlogin" style={{marginTop:"1%"}}>
        <div className="logo"> <img src="https://wallpapercave.com/wp/wp2123032.jpg" alt="" /> </div>
        <div className="text-center mt-4 name"> Josef </div>
        <form className="p-3 mt-3">
          <div className="form-field d-flex align-items-center"> <span className="fa fa-envelope" /> <input type="email" placeholder="Email Address" id="userName" /> </div>
          <div className="form-field d-flex align-items-center"> <span className="fas fa-key" /> <input type="password" name="password" id="pwd" placeholder="Password" /> </div> 
          <div className="form-field d-flex align-items-center"> <span className="fas fa-shield-alt" /> <input type="password" name="password" id="pwd" placeholder="Username" /> </div> 
          <button className="btn mt-3">Sign up</button>
        </form>
        <div className="text-center fs-6"> <a href="#">Forget password?</a> or <a href="/">Login</a> </div>
      </div>
  )
}

export default Register
